shrimp.rsl_code:
/* initialize */ $(Envcolor) = color(0); $(Envdir) = vector(0); $(Occ) = 0;
/* make sure options are clear */ /* and set them according to shadeop and renderer */ #ifdef RAYOPTIONS #undef RAYOPTIONS #endif
#if RENDERER == _3delight #define RAYOPTIONS "label", $(label), "subset", $(subset), \ "bias", $(bias), "hitmode", $(hitmode), \ "coneangle", $(coneangle), "axis", $(axis), \ "samplebase", $(samplebase), "maxdist", $(maxdist), \ "environmentmap", $(envmap), \ "environmentspace", $(envspace), \ "distribution", $(distribution), \ "adaptive", $(adapt), "minsamples", $(minsamp), \ "falloffmode", $(floffmode), "falloff", $(falloff), \ "pointbased", $(usept), "filename", $(pfile), \ "hitsides", $(hitsides), \ "maxsolidangle", $(maxsangle), "clamp", $(clamp), \ "sortbleeding", $(srtbleed), \ "coordsystem", $(coordsys), "occlusion", $(Occ) #elif RENDERER == pixie #define RAYOPTIONS "coordsystem", $(coordsys), "maxdist", $(maxdist), \ "coneangle", $(coneangle), \ "samplebase", $(samplebase), "label", $(label), \ "bias", $(bias), "maxerror", $(maxerror), \ "pointbased", $(usept), "environmentmap", $(envmap), \ "filename", $(pfile), "maxpixeldist", $(maxpixeldist), \ "maxsolidangle", $(maxsangle), "occlusion", $(Occ) #elif RENDERER == prman #define RAYOPTIONS "adapt", $(adapt), "minsamples", $(minsamp), \ "coordsystem", $(coordsys), \ "distribution", $(distribution), \ "maxdist", $(maxdist), "falloffmode", $(floffmode), \ "falloff", $(falloff), "coneangle", $(coneangle), \ "samplebase", $(samplebase), "hitmode", $(hitmode), \ "hitsides", $(hitsides), "subset", $(subset), \ "label", $(label), "environmentmap", $(envmap), \ "environmentspace", $(envspace), "bias", $(bias), \ "maxpixeldist", $(maxpixeldist), \ "maxerror", $(maxerror), "pointbased", $(usept), \ "filename", $(pfile), "maxsolidangle", $(maxsangle), \ "clamp", $(clamp), "environmentcolor", $(Envcolor), \ "environmentdir", $(Envdir), "occlusion", $(Occ) #elif RENDERER == air #define RAYOPTIONS "environmentmap", $(envmap), \ "environmentspace", $(envspace), "label", $(label), \ "subset", $(subset) #else /* no indirectdiffuse() available */ #define NOSHADEOP /* what about bmrt, entropy, renderdotc? */ #endif /* RENDERER */
#ifdef NOSHADEOP $(Ci) = 0; #else aov_indirectdiffuse += indirectdiffuse( $(inpoint), $(indir), $(samples), RAYOPTIONS ); aov_occlusion += $(Occ); $(Ci) = aov_indirectdiffuse; $(Occ) = 1 - $(Occ); #endif /* NOSHADEOP */
#undef NOSHADEOP #undef RAYOPTIONS
