shrimp.rsl_code:
#ifndef RAYOPTIONS #define RAYOPTIONS
/* default output */ $(dist) = 0; $(alpha) = 0;
#if RENDERER==pixie #define RAYOPTIONS "bias", $(bias), "samples", \ $(samples), "samplecone", $(coneangle) #elif RENDERER==_3delight #define RAYOPTIONS "label", $(label), "subset", $(subset), "bias", \ $(bias), "hitmode", $(hitmode), "maxdist", $(maxdist) #elif RENDERER==prman #define RAYOPTIONS "samplecone", $(coneangle), "bias", $(bias), "label", \ $(label), "subset", $(subset), "hitmode", $(hitmode), \ "hitsides", $(hitsides) #elif RENDERER==air #define RAYOPTIONS "bias", $(bias), "blur", $(blur), "label", $(label), \ "maxdist", $(maxdist), "samples", $(samples), \ "subset", $(subset), "majoraxis", $(majoraxis), \ "majorblur", $(majorblur), "weight", $(weight), \ "alpha", $(alpha) #else #define RAYOPTIONS "bias", $(bias) #endif
#endif
#define $(val)_$(val:type) #ifdef $(val)_float $(val) = $(val:type) trace( $(inpoint), $(indir) ); #else $(val) = $(val:type) trace( $(inpoint), $(indir), RAYOPTIONS ); #endif
