shrimp.code:
#define $(val)_$(var_type)
#ifdef $(val)_float
	$(val) = ($(bias) > 0) ? pow($(nput), log($(bias)) / log(0.5)) : 0;
#else
	float xtmp = xcomp( $(var_type) $(nput));
	float ytmp = ycomp( $(var_type) $(nput));
	float ztmp = zcomp( $(var_type) $(nput));
	$(val) = $(var_type) (bias(xtmp, $(bias)), bias(ytmp, $(bias)), bias(ztmp, $(bias)));
#endif
