shrimp.code:
#define $(val)_$(val:type)
#ifdef $(val)_float
	$(val) = gain($(nput), $(gain));
#else
	float xtmp = comp( $(val:type) $(nput), 0);
	float ytmp = comp( $(val:type) $(nput), 1);
	float ztmp = comp( $(val:type) $(nput), 2);
	$(val) = $(val:type) (gain(xtmp, $(gain)), gain(ytmp, $(gain)), gain(ztmp, $(gain)));
#endif
